00108         public SerializationFlags OnNetSerialize(int frameId, byte[] buffer, ref int bitposition, SerializationFlags writeFlags)
00109         {
00110             
00111             var frame = frames[frameId];
00112 
00113             bool sendReliable = frame.ownerHasChanged && (reliableOwnerChange || keyframeRate == 0 || (writeFlags & SerializationFlags.NewConnection) != 0);
00114             bool hascontent = sendReliable || IsKeyframe(frameId);
00115 
00116             //if (sendReliable)
00117             //    Debug.LogError(frameId + " OWNER " + frame.ownerActorId + " " + frame.ownerHasChanged);
00118 
00119             if (!hascontent)
00120             {
00121                 buffer.Write(0, ref bitposition, 1);
00122                 return SerializationFlags.None;
00123             }
00124 
00125             buffer.Write(1, ref bitposition, 1);
00126 
00127             var flags = SerializationFlags.HasContent;
00128 
00129             buffer.WritePackedBytes((uint)frame.ownerActorId, ref bitposition, 32);
00130 
00131             if (sendReliable)
00132                 flags |= SerializationFlags.ForceReliable;
00133 
00134             return flags;
00135         }
