00137         public SerializationFlags OnNetDeserialize(int originFrameId, byte[] buffer, ref int bitposition, FrameArrival frameArrival)
00138         {
00139             var frame = frames[originFrameId];
00140 
00141             // First read content, hold off on applying content to frame until we are sure we want to keep it.
00142             bool hascontent = buffer.Read(ref bitposition, 1) != 0;
00143             int ownerActorId = hascontent ? (int)buffer.ReadPackedBytes(ref bitposition, 32) : -1;
00144             
00145             // Ignore incoming updates if we are the current owner and the source is not the recognized controller. 
00146             // Leaves frame as it was - since this might overwrite valid data.
00147             if (photonView.IsMine) // && hascontent && ownerActorId !=  && photonView.ControllerActorNr != netObj.originHistory[originFrameId])
00148             {
00149                 //Debug.LogError("fid:" + originFrameId + " DES IGNORE newown: " + frame.ownerActorId);
00150                 return SerializationFlags.None;
00151             }
00152 
00153             if (hascontent)
00154             {
00155                 frame.content = FrameContents.Complete;
00156                 frame.ownerActorId = ownerActorId;
00157                 //Debug.LogError("fid:" + originFrameId + " DES newown: " + frame.ownerActorId + " cur:" + NetMaster.CurrentFrameId + " ctr:" + photonView.OwnerActorNr + ":" + photonView.ControllerActorNr) ;
00158 
00159                 return SerializationFlags.HasContent;
00160             }
00161             else
00162             {
00163                 frame.content = FrameContents.Empty;
00164                 return SerializationFlags.None;
00165             }
00166         }
