00221         public void OnIncrementFrame(int newFrameId, int newSubFrameId, int previousFrameId, int prevSubFrameId)
00222         {
00223             if (photonView.IsMine)
00224                 return;
00225 
00226             if (newSubFrameId != 0)
00227                 return;
00228 
00229             //Debug.LogError(ticksUntilOwnershipRetry);
00230 
00231             if (ticksUntilOwnershipRetry >= 0)
00232             {
00233                 if (ticksUntilOwnershipRetry == 0)
00234                 {
00235                     // Fallback ownership transfer in case our tick based method suffered horrendous loss.
00236                     Debug.LogError(name + " FALLBACK OWNER CHANGE " + photonView.ControllerActorNr);
00237                     photonView.TransferOwnership(photonView.Controller);
00238                     ticksUntilOwnershipRetry = TickEngineSettings.frameCount;
00239                 }
00240                 else
00241                 {
00242                     ticksUntilOwnershipRetry--;
00243                 }
00244             }
