00262         public virtual SerializationFlags OnNetSerialize(int frameId, byte[] buffer, ref int bitposition, SerializationFlags writeFlags)
00263         {
00264             
00266 
00267             Frame frame = frames[frameId];
00268             SerializationFlags flags = SerializationFlags.None;
00269 
00270             bool iskeyframe = IsKeyframe(frameId);
00271 
00272             if (!iskeyframe)
00273                 return flags;
00274 
00275             if (respawnEnable)
00276             {
00278                 int ticks = frame.ticksUntilRespawn;
00279 
00280                 if (ticks >= 0)
00281                 {
00283                     buffer.WriteBool(true, ref bitposition);
00284                     buffer.Write((ulong)ticks, ref bitposition, bitsForTicksUntilRespawn);
00285                     flags |= SerializationFlags.HasContent;
00286                 }
00287                 else
00288                 {
00290                     buffer.WriteBool(false, ref bitposition);
00291                 }
00292             }
00293 
00294             if (despawnEnable)
00295             {
00297                 int ticks = frame.ticksUntilDespawn;
00298                 if (ticks >= 0)
00299                 {
00301                     buffer.WriteBool(true, ref bitposition);
00302                     buffer.Write((ulong)ticks, ref bitposition, bitsForTicksUntilDespawn);
00303 
00304                     flags |= SerializationFlags.HasContent;
00305                 }
00306                 else
00307                 {
00309                     buffer.WriteBool(false, ref bitposition);
00310                 }
00311             }
