00316         public SerializationFlags OnNetDeserialize(int originFrameId, byte[] buffer, ref int bitposition, FrameArrival frameArrival)
00317         {
00318            
00319             Frame frame = frames[originFrameId];
00320             SerializationFlags flags = SerializationFlags.None;
00321 
00322             bool iskeyframe = IsKeyframe(originFrameId);
00323             if (!iskeyframe)
00324             {
00325                 frame.content = FrameContents.Empty;
00326                 return flags;
00327             }
00328 
00329             if (respawnEnable)
00330             {
00332                 if (buffer.ReadBool(ref bitposition))
00333                 {
00334                     frame.ticksUntilRespawn = (int)buffer.Read(ref bitposition, bitsForTicksUntilRespawn);
00335                     flags |= SerializationFlags.HasContent;
00336                 }
00337                 else
00338                     frame.ticksUntilRespawn = -1;
00339             }
00340 
00341             if (despawnEnable)
00342             {
00343                 if (buffer.ReadBool(ref bitposition))
00344                 {
00345                     frame.ticksUntilDespawn = (int)buffer.Read(ref bitposition, bitsForTicksUntilDespawn);
00346                     flags |= SerializationFlags.HasContent;
00347                 }
00348                 else
00349                     frame.ticksUntilDespawn = -1;
00350             }
00351 
00352             frame.content = FrameContents.Complete;
00353             return flags;
00354         }
