00358         protected override void ApplySnapshot(Frame snapframe, Frame targframe, bool snapIsValid, bool targIsValid)
00359         {
00361             if (snapIsValid && snapframe.content > FrameContents.Empty)
00362             {
00363                 //Debug.Log("snap change  tr: " + snapFrame.ticksUntilRespawn + " td:" + snapFrame.ticksUntilDespawn);
00364                 if (respawnEnable)
00365                     ticksUntilRespawn = snapframe.ticksUntilRespawn;
00366                 if (despawnEnable)
00367                     ticksUntilDespawn = snapframe.ticksUntilDespawn;
00368             }
00369             else
00370             {
00371                 //Debug.Log("snap no change");
00372                 if (respawnEnable)
00373                 {
00374                     ticksUntilRespawn--;
00375                     targframe.ticksUntilRespawn = ticksUntilRespawn;
00376                 }
00377                 if (despawnEnable)
00378                 {
00379                     ticksUntilDespawn--;
00380                     targframe.ticksUntilDespawn = ticksUntilDespawn;
00381                 }
00382             }
00383 
00384             if (photonView.IsMine)
00385             {
00386                 if (respawnEnable && ticksUntilRespawn == 0)
00387                 {
00388                     syncState.Respawn(false);
00389                     //Debug.Log("snap change  tr: " + snapFrame.ticksUntilRespawn + " td:" + snapFrame.ticksUntilDespawn);
00390                 }
00391 
00392                 if (despawnEnable && ticksUntilDespawn == 0)
00393                 {
00394                     syncState.Despawn(false);
00395                     //Debug.Log("snap change  tr: " + ticksUntilRespawn + ":" + snapFrame.ticksUntilRespawn + " td: " + ticksUntilRespawn + " " + snapFrame.ticksUntilDespawn);
00396                 }
00397 
00398             }
00399         }
