00271             ObjState newstate = attachTo ? (currentState.state & ~MOUNT_REM_FLAGS) | MOUNT_ADD_FLAGS : currentState.state & ~MOUNT_ADD_FLAGS;
00272             //QueueStateChange(newstate, attachTo, false);
