00393         protected virtual void ChangeState(StateChangeInfo stateChangeInfo)
00394         {
00395             if (!gameObject)
00396             {
00397                 Debug.LogWarning(name + " has been destroyed. Will not try to change state.");
00398                 return;
00399             }
00400 
00401             //if (photonView.IsMine && iSpawnController != null && iSpawnController.SupressStateMask != ~ObjState.Despawned)
00402             //{
00403             //    Debug.Log("<color=red>Suppressing State change due to spawn timer</color>");
00404             //    stateChangeInfo.objState &= iSpawnController.SupressStateMask;
00405             //}
00406 
00407 
00408             //if (GetComponent<SyncPickup>())
00409             //    Debug.LogError(Time.time + " " + name + " <b>ChangeState " + stateChangeInfo + "</b> par: " + currentMount + " tr: " + transform.position + " rb: " + Rb.position);
00410 
00411             var oldState = currentState.state;
00412             var oldMount = currentMount;
00413             var newState = stateChangeInfo.objState;
00414             var newMount = stateChangeInfo.mount;
00415 
00416             bool respawn;
00418             if (autoReset && oldState == ObjState.Despawned && newState != ObjState.Despawned && (newState & ObjState.Anchored) == 0)
00419             {
00420                 stateChangeInfo = new StateChangeInfo(respawnStateInfo) { objState = stateChangeInfo.objState };
00421                 respawn = true;
00422             }
00423             else
00424                 respawn = false;
00425 
00426             var force = stateChangeInfo.force;
00427 
00428             bool stateChanged = newState != oldState;
00429             bool mountChanged = oldMount != newMount;
00430 
00431             var prevParent = transform.parent;
00432 
00433 
00435             if (!force && !stateChanged && !mountChanged)
00436             {
00437                 //if (GetComponent<SyncPickup>())
00438                 //    Debug.LogError("NO CHANGE");
00439                 return;
00440             }
00441 
00442 
00443             currentState.state = newState;
00444             var prevMount = currentMount;
00445             currentMount = newMount;
00446 
00447             //if (GetComponent<SyncPickup>())
00448             //    Debug.LogError("<b>ChangeState </b>" + currentState + " par: " + currentMount);
00449 
00450             bool nowMounted = (newState & ObjState.Mounted) != 0;
00451             bool mountInfoWasCulled = nowMounted && ReferenceEquals(newMount, null);
00452 
00453             // TODO: Might not be needed.
00455             if (nowMounted && !mountInfoWasCulled && newMount == null)
00456             {
00457                 Debug.LogError("Invalid Mount!");
00458                 InvalidMountHandler(newState, newMount, force);
00459                 return;
00460             }
00461 
00462             if (IsMine)
00463                 if (mountChanged || respawn)
00464                 {
00465                     //Debug.LogError("TELEPORT");
00466                     for (int i = 0; i < flagTeleportCallbacks.Count; ++i)
00467                         flagTeleportCallbacks[i].FlagTeleport();
00468                 }
00469 
00471             /*else*/
00472             if (mountChanged)
00473             {
00475                 // If Attached bit is true
00476                 if (nowMounted)
00477                 {
00478 #if PUN_2_OR_NEWER
00479                     //if (GetComponent<SyncPickup>() /*&& newState == (ObjState)(15)*/)
00480                     //    Debug.LogError(Time.time + " " + name + " " + newState + " ATTACH " + newMount.name + " ");
00481 
00482                     currentState.mountToViewID = newMount.ViewID;
00483                     currentState.mountTypeId = newMount.mountType.id;
00484 #endif
00485                     transform.parent = newMount.transform;
00486 
00487                     // If anchor bit is true
00488                     if ((newState & ObjState.AnchoredPosition) != 0)
00489                     {
00490                         transform.localPosition = new Vector3();
00491                         //Debug.LogError("<b>Anchor Pos</b> " + transform.parent.name + " " + transform.localPosition);
00492                     }
00493 
00494                     // If anchor bit is true
00495                     if ((newState & ObjState.AnchoredRotation) != 0)
00496                     {
00497                         transform.localRotation = new Quaternion();
00498                         //Debug.Log("<b>Anchor Rot</b>");
00499                     }
00500                 }
00501 
00503                 else
00504                 {
00505                     //if (GetComponent<SyncPickup>() /*&& newState == (ObjState)(15)*/)
00506                     //    Debug.Log(Time.time + " " + name + " " + newState + " DETTACH " + (newMount ? newMount.name : " null"));
00507 
00508                     currentState.mountToViewID = null;
00509                     currentState.mountTypeId = null;
00510                     transform.parent = null;
00511                 }
00512 
00513                 // TODO: Add handling for dismounting returning ownership to scene?
00514                 // Ownership changes are deferred, to let the current tick finish processing before changing Owner/Controller.
00515                 if (autoOwnerChange && newMount && !ReferenceEquals(oldMount, newMount))
00516                 {
00517 
00518                     ChangeOwnerToParentMountsOwner();
00519 
00520                 }
00521                 Mount.ChangeMounting(this, prevMount, newMount);
00522             }
00523 
00524             var pos = stateChangeInfo.offsetPos;
00525             var rot = stateChangeInfo.offsetRot;
00526             var vel = stateChangeInfo.velocity;
00527 
00528             if (rot.HasValue)
00529             {
00530                 //if (GetComponent<SyncPickup>())
00531                 //    Debug.Log(Time.time + " " + name + " STATE ROT APPLY " + rot.Value);
00532 
00533                 transform.rotation = rot.Value;
00534             }
00535 
00536             // TODO: Move pos/rot handling to virtual method
00537             if (pos.HasValue)
00538             {
00539                 //if (GetComponent<SyncPickup>())
00540                 //    Debug.Log(Time.time + " " + name + " STATE POS APPLY " + pos.Value);
00541 
00542                 transform.position = pos.Value;
00543 
00544             }
00545 
00546             if (vel.HasValue)
00547             {
00548 
00549                 var rb = netObj.Rb;
00550                 if (rb)
00551                 {
00552                     rb.velocity = vel.Value;
00553                 }
00554                 else
00555                 {
00556                     var rb2d = netObj.Rb2D;
00557 
00558                     if (rb2d)
00559                     {
00560 
00561                         rb2d.velocity = vel.Value;
00562                     }
00563                 }
00564             }
00565 
00567             //this.ApplyVectors(stateChangeInfo, prevParent, onTeleportCallbacks);
00568 
00569             //Debug.Log(Time.time + " " + stateChangeInfo + " mountisactive? " + (currentMount ? currentMount.isActiveAndEnabled.ToString() : "null"));
00570 
00571             if (mountChanged || stateChanged || force)
00572             {
00573                 //if (GetComponent<SyncPickup>())
00574                 //    Debug.Log(Time.time + " " + name +  " " + photonView.OwnerActorNr + " <b>NEW STATE: " + newState + "</b> ready? " + netObj.AllObjsAreReady);
00575 
00577                 for (int i = 0; i < onStateChangeCallbacks.Count; ++i)
00578                     onStateChangeCallbacks[i].OnStateChange(newState, oldState, transform, currentMount, netObjIsReady);
00579             }
00580 
00581         }
