00610         private void ChangeOwnerToParentMountsOwner()
00611         {
00612             if (!IsMine)
00613                 return;
00614 
00615             if (!syncOwner)
00616             {
00617                 Debug.LogWarning(name + " cannot automatically change owner without a " + typeof(SyncOwner).Name + " component.");
00618                 return;
00619             }
00620 
00621             // We are only changing ownership if there is a mount. 
00622             // This null test is for the edge case of throwing at the same time as colliding with another players object creates a race condition and the owner ends up being null/0
00623             if (currentMount == null)
00624                 return;
00625 
00626             PhotonView mountView = currentMount.PhotonView;
00627 
00628             //Debug.LogError(Time.time + " DEFERRED New Owner " + (mountView ? mountView.name + ":" + mountView.Owner.ToString() : "null"));
00629 
00630             if (mountView)
00631             {
00632                 Realtime.Player pendingOwner = mountView.Owner;
00633                 int pendingOwnerId = pendingOwner == null ? 0 : pendingOwner.ActorNumber;
00634 
00635                 //photonView.SetOwnerInternal(pendingOwner, pendingOwnerId);
00636                 if (this.autoOwnerChange)
00637                     this.syncOwner.TransferOwner(pendingOwnerId);
00638             }
00639             else
00640             {
00641                 //photonView.SetOwnerInternal(null, 0);
00642                 if (this.autoOwnerChange)
00643                     GetComponent<SyncOwner>().TransferOwner(0);
00644 
00645             }
00646 
00647             // TEST invalidate all netobj buffers on ownership change
00648             //netObj.frameValidMask.SetAllFalse();
00649         }
