00664         public virtual bool ChangeMount(int newMountId)
00665         {
00666             if (ReferenceEquals(currentMount, null))
00667             {
00668                 Debug.LogWarning("'" + name + "' is not currently mounted, so we cannot change to a different mount.");
00669                 return false;
00670             }
00671 
00672             if ((mountableTo & (1 << newMountId)) == 0)
00673             {
00674                 Debug.LogWarning("'" + name + "' is trying to switch to a mount '" + MountSettings.GetName(newMountId) + "' , but mount is not set as valid in SyncState.");
00675                 return false;
00676             }
00677 
00678             var lookup = currentMount.mountsLookup.mountIdLookup;
00679 
00680             if (!lookup.ContainsKey(newMountId))
00681             {
00682                 Debug.LogWarning("'" + name + "' doesn't contain a mount for '" + MountSettings.GetName(newMountId) + "'.");
00683                 return false;
00684             }
00685             var attachTo = lookup[newMountId];
00686 
00687             //Debug.Log("New Mount:" + attachTo + ":" + newMountId);
00688 
00689             ChangeState(new StateChangeInfo(currentState.state, attachTo, false));
00690 
00691             return true;
00692         }
