00714 
00715         // TODO: May not be needed due to has changed checks
00716         protected Frame prevSerializedFrame;
00717 
00718         public SerializationFlags OnNetSerialize(int frameId, byte[] buffer, ref int bitposition, SerializationFlags writeFlags)
00719         {
00720 
00721             //if (false)
00724             if (!enabled) // This was ActiveAndEnabled... but that is a problem if mounting immediately to a spawned object, as it is disabled for some reason.
00725             {
00726                 // leading bool
00727                 buffer.WriteBool(false, ref bitposition);
00728                 return SerializationFlags.None;
00729             }
00730 
00731             Frame frame = frames[frameId];
00732 
00733             bool iskeyframe = IsKeyframe(frameId);
00734             bool isNewConnection = (writeFlags & SerializationFlags.NewConnection) != 0;
00735             bool isFirstSend = ReferenceEquals(prevSerializedFrame, null);
00736             bool stateHasChanged = isFirstSend || frame.state != prevSerializedFrame.state;
00737             //bool parentHasChanged = isFirstSend || frame.attachedToViewID != prevSerializedFrame.attachedToViewID;
00738             bool mountHasChanged = isFirstSend || frame.mountTypeId != prevSerializedFrame.mountTypeId || frame.mountToViewID != prevSerializedFrame.mountToViewID;
00739             bool needsComplete = isNewConnection || iskeyframe || isFirstSend;
00740             bool hasChanged = stateHasChanged || mountHasChanged;
00741             bool hasContent = needsComplete || hasChanged;
00742             bool forceReliable = isFirstSend || isNewConnection || (keyframeRate == 0 && hasContent) || (mountReliable && mountHasChanged);
00743 
00744             // If we determined there is nothing to send, first bit 0 and exit
00745             if (!hasContent && !forceReliable)
00746             {
00747                 // leading bool
00748                 buffer.WriteBool(false, ref bitposition);
00749                 return SerializationFlags.None;
00750             }
00751 
00752             // Leading bool
00753             buffer.WriteBool(true, ref bitposition);
00754 
00755             var flags = SerializationFlags.HasContent;
00756 
00757             // Determine if anything requires this be made a Reliable send
00758             if (forceReliable)
00759             {
00760                 //if (GetComponent<SyncPickup>())
00761                 //    Debug.LogError(Time.time + " <b>" +  frame.frameId + ":" + frameId + "</b> STATE " + isFirstSend + " " + isNewConnection + " " + (keyframeRate == 0 && hasContent) + " " + (mountReliable && mountHasChanged) + " " + frame.state);
00762 
00763                 flags |= SerializationFlags.ForceReliable;
00764             }
00765 
00767             buffer.Write((ulong)frame.state, ref bitposition, 6);
00768 
00769             if ((frame.state & ObjState.Mounted) != 0)
00770             {
00771                 if (mountHasChanged || needsComplete)
00772                 {
00774                     if (!iskeyframe)
00775                         buffer.Write(1, ref bitposition, 1);
00776 
00777                     //if (GetComponent<SyncPickup>())
00778                     //    Debug.LogError(frameId + " Write Parent " + frame.mountToViewID);
00779 
00780                     // Write Parent viewId
00781                     buffer.WritePackedBytes((uint)frame.mountToViewID, ref bitposition, 32);
00782 
00783                     // If there is more than one mount type this is allowed to mount to, indicate the mount type index (not the index on the parent, the index from allowed types for this SyncState)
00784                     if (bitsForMountType > 0)
00785                     {
00786                         var mountidx = mountTypeIdToIndex[frame.mountTypeId.Value];
00787                         buffer.Write((uint)mountidx, ref bitposition, bitsForMountType);
00788                     }
00789                 }
00790                 else
00791                 {
00793                     if (!iskeyframe)
00794                         buffer.Write(0, ref bitposition, 1);
00795                 }
00796             }
00797 
00798             //if (GetComponent<SyncPickup>())
00799             //    Debug.Log(frameId + " <b>SER</b> " + flags + " " + frame.state + " " + frame.attachedToViewID + " : " + frame.attachedToMountTypeId + " mounthaschagned: " + mountHasChanged + " needscomplete: " + needsComplete );
00800 
00801             prevSerializedFrame = frame;
00802             return flags;
00803         }
00804 
00805         public SerializationFlags OnNetDeserialize(int originFrameId, byte[] buffer, ref int bitposition, FrameArrival arrival)
00806         {
00807 
00808             Frame frame = frames[originFrameId];
00809 
00810             if (!buffer.ReadBool(ref bitposition))
00811             {
00812                 //if (GetComponent<SyncPickup>())
00813                 //    Debug.LogError(originFrameId + " No Content");
00814 
00815                 return SerializationFlags.None;
00816             }
00817 
00818             SerializationFlags flags = SerializationFlags.HasContent;
00819 
00821             frame.state = (ObjState)buffer.Read(ref bitposition, 6);
00822 
00823             bool isAttached = (frame.state & ObjState.Mounted) != 0;
00824 
00825             if (!isAttached)
00826             {
00827                 frame.content = FrameContents.Complete;
00828             }
00829             else if (IsKeyframe(originFrameId) || buffer.Read(ref bitposition, 1) == 1)
00830             {
00832                 if ((frame.state & ObjState.Mounted) != 0)
00833                 {
00834                     frame.mountToViewID = (int?)buffer.ReadPackedBytes(ref bitposition, 32);
00835                     if (bitsForMountType > 0)
00836                     {
00837                         int mountidx = (int)buffer.Read(ref bitposition, bitsForMountType);
00838                         int mountTypeId = indexToMountTypeId[mountidx];
00839                         frame.mountTypeId = (int?)mountTypeId;
00840                     }
00841                     else
00842                         frame.mountTypeId = 0;
00843                 }
00844                 frame.content = FrameContents.Complete;
00845             }
00846 
00848             else
00849             {
00850                 frame.mountToViewID = null;
00851                 frame.mountTypeId = null;
00852                 frame.content = FrameContents.Partial;
00853             }
00854 
00855             //if (GetComponent<SyncPickup>())
00856             //    Debug.Log(Time.time + " " + flags + " " + name + " <b> fr: " + frame.frameId + " <color=purple>DES STATE</color></b> " + frame.content + " " + frame.state +
00857             //        ((frame.state & ObjState.Visible) != 0 ? " VISIBLE " : " ") +
00858             //        ((frame.state & ObjState.Mounted) != 0 ? " MOUNTED " + frame.mountToViewID : " ")
00859             //        );
00860 
00861             return flags;
00862         }
