00886         protected override void ApplySnapshot(Frame snapframe, Frame targframe, bool snapIsValid, bool targIsValid)
00887         {
00888 
00889             if (snapframe.content == FrameContents.Empty)
00890             {
00891                 return;
00892             }
00893 
00896             if (targframe.content == FrameContents.Complete) // & FrameContents.Partial) != 0)
00897             {
00898                 Transform par;
00899                 if (targframe.mountToViewID.HasValue)
00900                 {
00901                     var targmount = GetMount(targframe.mountToViewID, targframe.mountTypeId);
00902                     par = targmount ? targmount.transform.parent : null;
00903                 }
00904                 else
00905                     par = null;
00906 
00907                 if (syncTransform)
00908                     syncTransform.UpdateParent(targframe.state, par);
00909             }
00910 
00911             //if (GetComponent<SyncPickup>() /*&& snapFrame.state == (ObjState)15*/)
00912             //    Debug.Log(Time.time + " " + name + " <color=green><b>APPLY STATE</b></color> content :" + snapFrame.content +
00913             //        "\n<b>snap: fid: " + snapFrame.frameId + "</b> snapState: " + (snapFrame.state + "  attchTo: " + snapFrame.mountToViewID + ":" + snapFrame.mountTypeId) +
00914             //        " <b>Targ: fid: " + targFrame.frameId + "</b> targState: " +
00915             //        (targFrame.content != 0 ? (targFrame.state + "  attchTo: " + targFrame.mountToViewID + ":" + targFrame.mountTypeId) : " lost") + " " + transform.localPosition + " " +
00916             //        netObj.AllObjsAreReady
00917             //        );
00918 
00919             if (snapframe.content >= FrameContents.Extrapolated)
00920                 ApplyFrame(snapframe);
00921         }
