00924         private void ApplyFrame(Frame frame)
00925         {
00926 
00927 
00928             var state = frame.state;
00929 
00930             Mount applyMount;
00931 
00932             bool mounted = ((state & ObjState.Mounted) != 0);
00933             bool force = false;
00934 
00935             if (mounted)
00936             {
00937                 int? snapAttachedToViewID = frame.mountToViewID;
00938 
00940                 if (snapAttachedToViewID.HasValue)
00941                 {
00942                     int? snapAttachedToMountId = frame.mountTypeId;
00943 
00944                     var mount = GetMount(snapAttachedToViewID, snapAttachedToMountId);
00945 
00946                     // Exit if state has not changed.
00947                     if (ReferenceEquals(mount, currentMount) && state == currentState.state)
00948                         return;
00949 
00950                     if (mount)
00951                     {
00952                         //if (autoOwnerChange && !ReferenceEquals(currentMount, mount))
00953                         //    TransferToMountOwner(mount);
00954 
00955                         applyMount = mount;
00956                         ReadyState = ReadyStateEnum.Ready;
00957                         force = true;
00958                     }
00959                     else
00960                         applyMount = currentMount;
00961 
00962                     //if (GetComponent<SyncPickup>())
00963                     //    Debug.Log(Time.time + " " + name + " snapMount: " + snapMount);
00964 
00965                 }
00967                 else
00968                 {
00970                     if (currentMount == null)
00971                     {
00972                         //if (GetComponent<SyncPickup>())
00973                         //  Debug.Log("<color=red>UNREADY</color>");
00974 
00975                         //ReadyState = ReadyStateEnum.Unready;
00976                         applyMount = null;
00977                     }
00978                     else
00979                     {
00980                         ReadyState = ReadyStateEnum.Ready;
00981                         applyMount = currentMount;
00982                         force = true;
00983                     }
00984                 }
00985             }
00987             else
00988             {
00989                 ReadyState = ReadyStateEnum.Ready;
00990                 force = true;
00991                 applyMount = null;
00992             }
00993 
00994             //if (syncTransform && attach || snapMount != null)
00995             //  syncTransform.UpdateParent(targFrame.state, null);
00996             //if (GetComponent<SyncPickup>())
00997             //    Debug.Log("Snap " + snapState + " " + snapFrame.attachedToViewID + ":" + snapFrame.attachedToMountTypeId + " --- " + snapMount);
00998 
00999             ChangeState(new StateChangeInfo(state, applyMount, force/*snapFrame.content == FrameContents.Complete*/));
01000         }
