01003         public static Mount GetMount(int? viewID, int? mountId)
01004         {
01005             if (!viewID.HasValue || !mountId.HasValue)
01006                 return null;
01007 
01008             var pv = PhotonNetwork.GetPhotonView(viewID.Value);
01009             var mounts = pv ? pv.GetComponent<MountsManager>() : null;
01010 
01011             if (mounts)
01012                 return mounts.mountIdLookup[mountId.Value];
01013 
01014             return null;
01015         }
