00052         public static SyncTransform AddDefaultDisabledSyncTransform(Transform t)
00053         {
00054             if (ReferenceEquals(t, null))
00055                 t = Selection.activeTransform;
00056 
00057             if (ReferenceEquals(t, null))
00058                 return null;
00059 
00060             SyncTransform st = t.GetComponent<SyncTransform>();
00061             if (!st)
00062             {
00063                 st = t.gameObject.AddComponent<SyncTransform>();
00064                 Debug.Log("Added SyncTransform to " + t.name);
00065                 var tc = st.transformCrusher;
00066                 var pc = tc.PosCrusher;
00067                 var rc = tc.RotCrusher;
00068                 var sc = tc.SclCrusher;
00069 
00070                 pc.XCrusher.Enabled = false;
00071                 pc.YCrusher.Enabled = false;
00072                 pc.ZCrusher.Enabled = false;
00073 
00074                 rc.TRSType = TRSType.Euler;
00075                 rc.XCrusher.Enabled = false;
00076                 rc.YCrusher.Enabled = false;
00077                 rc.ZCrusher.Enabled = false;
00078 
00079                 sc.uniformAxes = ElementCrusher.UniformAxes.NonUniform;
00080                 sc.XCrusher.Enabled = false;
00081                 sc.YCrusher.Enabled = false;
00082                 sc.ZCrusher.Enabled = false;
00083             }
00084 
00085             return st;
00086         }
