00169         public Consumption TryTrigger(IContactReactor icontactReactor, ContactEvent contactEvent, int compatibleMounts)
00170         {
00171             var reactor = (icontactReactor as IVitalsContactReactor);
00172 
00173             if (ReferenceEquals(reactor, null))
00174                 return Consumption.None;
00175 
00177             if (contactGroups != 0)
00178             {
00179                 //var groups = (reactor as Component).GetComponent<ContactGroupAssign>();
00180                 IContactGroupsAssign groups = contactEvent.contactTrigger.ContactGroupsAssign;
00181 
00182                 int triggermask = ReferenceEquals(groups, null) ? 0 : groups.Mask;
00183                 if ((contactGroups.Mask & triggermask) == 0)
00184                 {
00185 #if UNITY_EDITOR
00186                     Debug.Log(name + " SyncVitals.TryTrigger() ContactGroup Mismatch. Cannot pick up '" + (contactEvent.contactTrigger as Component).transform.root.name + "' because its has a non-matching ContactGroupAssign.");
00187 #endif
00188                     return Consumption.None;
00189                 }
00190             }
00191 
00193             if ((compatibleMounts != defaultMountingMask) && (compatibleMounts & defaultMountingMask) == 0)
00194                 return Consumption.None;
00195 
00196             Vital vital = vitals.GetVital(reactor.VitalNameType);
00197             if (vital == null)
00198             {
00199                 return Consumption.None;
00200             }
00201 
00203 
00204             //double charge = vpr.Charge;
00205             Consumption consumed;
00206 
00207             double amountConsumed;
00208             {
00210                 if (IsMine)
00211                 {
00212                     double discharge = reactor.DischargeValue(contactEvent.contactType);
00213                     amountConsumed = vitals.ApplyCharges(discharge, reactor.AllowOverload, reactor.Propagate);
00214                 }
00216                 else
00217                 {
00218                     amountConsumed = vital.TestApplyChange(reactor, contactEvent);
00219                 }
00220             }
00221 
00222             var consumable = icontactReactor as IVitalsConsumable;
00223             if (!ReferenceEquals(consumable, null))
00224                 consumed = TestConsumption(amountConsumed, consumable, contactEvent);
00225             else
00226                 consumed = Consumption.None;
00227 
00228             return consumed;
00229         }
00230 
00231         protected Consumption TestConsumption(double amountConsumed, IVitalsConsumable iva, ContactEvent contactEvent)
00232         {
00233 
00234             var consumption = iva.Consumption;
00235             var discharge = iva.DischargeValue(contactEvent.contactType);
00236 
00237             if (consumption == Consumption.None)
00238                 return Consumption.None;
00239 
00240             if (consumption == Consumption.All)
00241             {
00242                 if (amountConsumed != 0)
00243                 {
00244                     iva.Charges = 0;
00245                     return Consumption.All;
00246                 }
00247                 return Consumption.None;
00248             }
00249 
00250             var consumed = amountConsumed == 0 ? Consumption.None : discharge == amountConsumed ? Consumption.All : Consumption.Partial;
00251             iva.Charges -= amountConsumed;
00252             return consumed;
00253         }
