00152         private void CameraRotation()
00153         {
00154             // if there is an input and camera position is not fixed
00155             if (_input.look.sqrMagnitude >= _threshold && !LockCameraPosition)
00156             {
00157                 _cinemachineTargetYaw += _input.look.x * Time.deltaTime;
00158                 _cinemachineTargetPitch += _input.look.y * Time.deltaTime;
00159             }
00160 
00161             // clamp our rotations so our values are limited 360 degrees
00162             _cinemachineTargetYaw = ClampAngle(_cinemachineTargetYaw, float.MinValue, float.MaxValue);
00163             _cinemachineTargetPitch = ClampAngle(_cinemachineTargetPitch, BottomClamp, TopClamp);
00164 
00165             // Cinemachine will follow this target
00166             CinemachineCameraTarget.transform.rotation = Quaternion.Euler(_cinemachineTargetPitch + CameraAngleOverride, _cinemachineTargetYaw, 0.0f);
