00169         private void Move()
00170         {
00171             // set target speed based on move speed, sprint speed and if sprint is pressed
00172             float targetSpeed = _input.sprint ? SprintSpeed : MoveSpeed;
00173 
00174             // a simplistic acceleration and deceleration designed to be easy to remove, replace, or iterate upon
00175 
00176             // note: Vector2's == operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00177             // if there is no input, set the target speed to 0
00178             if (_input.move == Vector2.zero) targetSpeed = 0.0f;
00179 
00180             // a reference to the players current horizontal velocity
00181             float currentHorizontalSpeed = new Vector3(_controller.velocity.x, 0.0f, _controller.velocity.z).magnitude;
00182 
00183             float speedOffset = 0.1f;
00184             float inputMagnitude = _input.analogMovement ? _input.move.magnitude : 1f;
00185 
00186             // accelerate or decelerate to target speed
00187             if (currentHorizontalSpeed < targetSpeed - speedOffset || currentHorizontalSpeed > targetSpeed + speedOffset)
00188             {
00189                 // creates curved result rather than a linear one giving a more organic speed change
00190                 // note T in Lerp is clamped, so we don't need to clamp our speed
00191                 _speed = Mathf.Lerp(currentHorizontalSpeed, targetSpeed * inputMagnitude, Time.deltaTime * SpeedChangeRate);
00192 
00193                 // round speed to 3 decimal places
00194                 _speed = Mathf.Round(_speed * 1000f) / 1000f;
00195             }
00196             else
00197             {
00198                 _speed = targetSpeed;
00199             }
00200             _animationBlend = Mathf.Lerp(_animationBlend, targetSpeed, Time.deltaTime * SpeedChangeRate);
00201 
00202             // normalise input direction
00203             Vector3 inputDirection = new Vector3(_input.move.x, 0.0f, _input.move.y).normalized;
00204 
00205             // note: Vector2's != operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00206             // if there is a move input rotate player when the player is moving
00207             if (_input.move != Vector2.zero)
00208             {
00209                 _targetRotation = Mathf.Atan2(inputDirection.x, inputDirection.z) * Mathf.Rad2Deg + _mainCamera.transform.eulerAngles.y;
00210                 float rotation = Mathf.SmoothDampAngle(transform.eulerAngles.y, _targetRotation, ref _rotationVelocity, RotationSmoothTime);
00211 
00212                 // rotate to face input direction relative to camera position
00213                 transform.rotation = Quaternion.Euler(0.0f, rotation, 0.0f);
00214             }
00215 
00216 
00217             Vector3 targetDirection = Quaternion.Euler(0.0f, _targetRotation, 0.0f) * Vector3.forward;
00218 
00219             // move the player
00220             _controller.Move(targetDirection.normalized * (_speed * Time.deltaTime) + new Vector3(0.0f, _verticalVelocity, 0.0f) * Time.deltaTime);
00221 
00222             // update animator if using character
00223             if (_hasAnimator)
00224             {
00225                 _animator.SetFloat(_animIDSpeed, _animationBlend);
00226                 _animator.SetFloat(_animIDMotionSpeed, inputMagnitude);
00227             }
00228         }
