00230         private void JumpAndGravity()
00231         {
00232             if (Grounded)
00233             {
00234                 // reset the fall timeout timer
00235                 _fallTimeoutDelta = FallTimeout;
00236 
00237                 // update animator if using character
00238                 if (_hasAnimator)
00239                 {
00240                     _animator.SetBool(_animIDJump, false);
00241                     _animator.SetBool(_animIDFreeFall, false);
00242                 }
00243 
00244                 // stop our velocity dropping infinitely when grounded
00245                 if (_verticalVelocity < 0.0f)
00246                 {
00247                     _verticalVelocity = -2f;
00248                 }
00249 
00250                 // Jump
00251                 if (_input.jump && _jumpTimeoutDelta <= 0.0f)
00252                 {
00253                     // the square root of H * -2 * G = how much velocity needed to reach desired height
00254                     _verticalVelocity = Mathf.Sqrt(JumpHeight * -2f * Gravity);
00255 
00256                     // update animator if using character
00257                     if (_hasAnimator)
00258                     {
00259                         _animator.SetBool(_animIDJump, true);
00260                     }
00261                 }
00262 
00263                 // jump timeout
00264                 if (_jumpTimeoutDelta >= 0.0f)
00265                 {
00266                     _jumpTimeoutDelta -= Time.deltaTime;
00267                 }
00268             }
00269             else
00270             {
00271                 // reset the jump timeout timer
00272                 _jumpTimeoutDelta = JumpTimeout;
00273 
00274                 // fall timeout
00275                 if (_fallTimeoutDelta >= 0.0f)
00276                 {
00277                     _fallTimeoutDelta -= Time.deltaTime;
00278                 }
00279                 else
00280                 {
00281                     // update animator if using character
00282                     if (_hasAnimator)
00283                     {
00284                         _animator.SetBool(_animIDFreeFall, true);
00285                     }
00286                 }
00287 
00288                 // if we are not grounded, do not jump
00289                 _input.jump = false;
00290             }
00291 
00292             // apply gravity over time if under terminal (multiply by delta time twice to linearly speed up over time)
00293             if (_verticalVelocity < _terminalVelocity)
00294             {
00295                 _verticalVelocity += Gravity * Time.deltaTime;
00296             }
00297         }
