00130         private void CalculateBufferValues()
00131         {
00133             if (_bufferCorrection == BufferCorrection.Manual)
00134             {
00135                 //targetBufferSize = single._targetBufferSize;
00136                 minBufferSize = _minBufferSize;
00137                 maxBufferSize = _maxBufferSize;
00138             }
00139             else
00140             {
00141                 minBufferSize = _minBufferSize;
00142                 maxBufferSize = _maxBufferSize;
00143             }
00144 
00145             frameCount = (int)_frameCount;
00146             halfFrameCount = frameCount / 2;
00147             thirdFrameCount = frameCount / 3;
00148             quaterFrameCount = frameCount / 4;
00149 
00150             frameCountBits = frameCount.GetBitsForMaxValue();
00151 
00152             netTickInterval = Time.fixedDeltaTime * _sendEveryXTick;
00153             netTickIntervalInv = 1f / (Time.fixedDeltaTime * _sendEveryXTick);
00154             targetBufferInterval = Time.fixedDeltaTime * _sendEveryXTick * _targetBufferSize;
00155 
00156             sendEveryXTick = _sendEveryXTick;
00157 
00158             targetBufferSize = _targetBufferSize;
00159             ticksBeforeGrow = _ticksBeforeGrow;
00160             ticksBeforeShrink = _ticksBeforeShrink;
00161 
00162             secondsOfBuffer = Time.fixedDeltaTime * _sendEveryXTick * frameCount;
00163             secondsOfHalfBuffer = secondsOfBuffer * .5f;
00164             bufferTargSecs = netTickInterval * _targetBufferSize;
00165         }
