00258         private void AutoSetBuffer(SerializedProperty frameCount, SerializedProperty sendEveryX)
00259         {
00260             float secondsPerTick = Time.fixedDeltaTime * sendEveryX.intValue;
00261 
00262             float framesNeeded = 1 / secondsPerTick;
00263 
00264 
00265             if (framesNeeded < (float)FrameCountEnum.FrameCount12)
00266                 frameCount.intValue = (int)FrameCountEnum.FrameCount12;
00267 
00268             else if (framesNeeded < (float)FrameCountEnum.FrameCount30)
00269                 frameCount.intValue = (int)FrameCountEnum.FrameCount30;
00270 
00271             else if (framesNeeded < (float)FrameCountEnum.FrameCount60)
00272                 frameCount.intValue = (int)FrameCountEnum.FrameCount60;
00273 
00274             else
00275                 frameCount.intValue = (int)FrameCountEnum.FrameCount120;
00276 
00277             //Debug.Log("Frames needed for 1 sec " + framesNeeded + " : " + secondsPerTick + " frameCount: " + FrameCount + " fixed: " + Time.fixedDeltaTime + " " + sendEveryX.intValue);
00278 
00279         }
