00061         public static void PreSnapshot(int currentFrameId)
00062         {
00063             
00064             for (int i = 0; i < connections.Count; ++i)
00065                 if (!ReferenceEquals(perConnOffsets[connections[i]], null))
00066                 {
00067                     var offsets = perConnOffsets[connections[i]];
00068 
00069                     var originFrameId = offsets.ConvertFrameLocalToOrigin(currentFrameId);
00070                     float timeBeforeConsumption = offsets.validFrameMask[originFrameId] ? Time.time - offsets.frameArriveTime[originFrameId] : -1;
00071 
00072                     offsets.frameTimeBeforeConsumption[originFrameId] = timeBeforeConsumption;
00073                     //Debug.Log(timeBeforeConsumption);
00074 
00075                     offsets.SnapshotAdvance();
00076                 }
00077         }
00078 
00079         public static void PostSnapshot(int currentFrameId)
00080         {
00081             for (int i = 0; i < connections.Count; ++i)
00082                 if (!ReferenceEquals(perConnOffsets[connections[i]], null))
00083                     perConnOffsets[connections[i]].PostSnapshot();
00084         }
