00091         public static ConnectionTickOffsets LogIncomingFrame(int connId, int originFrameId, out FrameArrival arrival)
00092         {
00093             ConnectionTickOffsets offsets;
00094 
00095             int frameCount = TickEngineSettings.frameCount;
00096 
00097             //Debug.Log("conn: " + connId + " fid: " + originFrameId);
00098 
00099             if (!perConnOffsets.TryGetValue(connId, out offsets) || offsets == null)
00100             {
00101                 LogNewConnection(connId, originFrameId, frameCount, out offsets);
00102             }
00103 
00104             int localFrameId = originFrameId + offsets.originToLocalFrame;
00105             if (localFrameId >= frameCount)
00106                 localFrameId -= frameCount;
00107 
00108             offsets.frameArriveTime[originFrameId] = Time.time;
00109 
00110             int currTargFrameId = NetMaster.CurrentFrameId; // offsets.currTargFrameId;
00111 
00112             //bool frameIsInFuture;
00113             int frameOffsetFromCurrentTarg;
00114 
00115             if (localFrameId == currTargFrameId)
00116             {
00117                 //frameIsInFuture = false;
00118                 frameOffsetFromCurrentTarg = 0;
00119             }
00120             else
00121             {
00123                 frameOffsetFromCurrentTarg = currTargFrameId - localFrameId;
00124                 if (frameOffsetFromCurrentTarg < 0)
00125                     frameOffsetFromCurrentTarg += frameCount;
00126                 if (frameOffsetFromCurrentTarg >= TickEngineSettings.halfFrameCount)
00127                     frameOffsetFromCurrentTarg -= frameCount;
00128 
00129                 //frameIsInFuture = frameOffsetFromCurrentTarg != 0 && frameOffsetFromCurrentTarg < TickEngineSettings.halfFrameCount;
00130             }
00131 
00132 #if UNITY_EDITOR
00133             const string STR_TAG = "\nSeeing many of these messages indicates a buffer underrun, due to an unstable connection, too small a buffer setting, or too high a tick rate.";
00134 #endif
00135 
00136             if (frameOffsetFromCurrentTarg >= 0)
00137             {
00138 
00139 
00140                 if (frameOffsetFromCurrentTarg == 0)
00141                 {
00142 #if UNITY_EDITOR
00143                     if (TickEngineSettings.LogLevel == TickEngineSettings.LogInfoLevel.All)
00144                     {
00145                         string strframes = " Incoming Frame: <b>" + originFrameId + "</b> Current Interpolation: " + offsets.ConvertFrameLocalToOrigin(NetMaster.PreviousFrameId) + "->" + offsets.ConvertFrameLocalToOrigin(currTargFrameId);
00146                         Debug.Log("<b>Late Update </b>conn: " + connId + " " + strframes + ". Already interpolating to this frame. Not critically late, but getting close." + STR_TAG);
00147                     }
00148 #endif
00149                 }
00150                 else if (frameOffsetFromCurrentTarg == 1)
00151                 {
00152 #if UNITY_EDITOR
00153                     if (TickEngineSettings.LogLevel >= TickEngineSettings.LogInfoLevel.WarningsAndErrors)
00154                     {
00155                         string strframes = " Incoming Frame: <b>" + originFrameId + "</b> Current Interpolation: " + offsets.ConvertFrameLocalToOrigin(NetMaster.PreviousFrameId) + "->" + offsets.ConvertFrameLocalToOrigin(currTargFrameId);
00156                         Debug.LogWarning("<b>Critically Late Update</b> conn: " + connId + " " + strframes + " Already applied and now interpolating from this frame. Snapshots will be rewound and reapplied." + STR_TAG);
00157                     }
00158 #endif
00159                 }
00160                 else if (frameOffsetFromCurrentTarg >= TickEngineSettings.halfFrameCount)
00161                 {
00162 #if UNITY_EDITOR
00163                     string strframes = " Incoming Frame: <b>" + originFrameId + "</b> Current Interpolation: " + offsets.ConvertFrameLocalToOrigin(NetMaster.PreviousFrameId) + "->" + offsets.ConvertFrameLocalToOrigin(currTargFrameId);
00164                     Debug.LogError("<b>Critically Late Update</b> conn: " + connId + " " + strframes + " DATA LOSS HAS OCCURRED EVEN FOR RELIABLE PACKETS. Increase Buffer size in "
00165                         + typeof(TickEngineSettings).Name + "." + STR_TAG);
00166 #endif
00167                 }
00168                 else
00169                 {
00170 #if UNITY_EDITOR
00171                     if (TickEngineSettings.LogLevel >= TickEngineSettings.LogInfoLevel.WarningsAndErrors)
00172                     {
00173                         string strframes = " Incoming Frame: <b>" + originFrameId + "</b> Current Interpolation: " + offsets.ConvertFrameLocalToOrigin(NetMaster.PreviousFrameId) + "->" + offsets.ConvertFrameLocalToOrigin(currTargFrameId);
00174                         Debug.LogWarning("<b>Critically Late Update</b> conn: " + connId + " " + strframes + " Already applied and now interpolating from this frame. Snapshots will be rewound and reapplied" + STR_TAG);
00175                     }
00176 #endif
00177                 }
00178             }
00179             else if (frameOffsetFromCurrentTarg <= (-(TickEngineSettings.halfFrameCount)))
00180             {
00181 #if UNITY_EDITOR
00182                 string strframes = " Incoming Frame: <b>" + originFrameId + "</b> Current Interpolation: " + offsets.ConvertFrameLocalToOrigin(NetMaster.PreviousFrameId) + "->" + offsets.ConvertFrameLocalToOrigin(currTargFrameId);
00183                 Debug.LogError("<b>Critically Late Update</b> conn: " + connId + " " + strframes + " DATA LOSS HAS OCCURRED EVEN FOR RELIABLE PACKETS. Increase Buffer size in "
00184                         + typeof(TickEngineSettings).Name + "." + STR_TAG);
00185 #endif
00186             }
00187 
00188             arrival = (FrameArrival)frameOffsetFromCurrentTarg;
00189 
00190             //Debug.Log(Time.time + " ARRIVAL " + arrival + " fid:" + originFrameId + " half: " + TickEngineSettings.halfFrameCount);
00191 
00192             bool frameIsInFuture = frameOffsetFromCurrentTarg <= 0;
00193 
00194             offsets.frameArrivedTooLate |= !frameIsInFuture;
00195             offsets.validFrameMask.Set(originFrameId, true /*frameIsInFuture*/);
00196 
