00200         private static void LogNewConnection(int connId, int originFrameId, int frameCount, out ConnectionTickOffsets offsets)
00201         {
00202             int currentFrame = NetMaster.CurrentFrameId;
00203 
00205             int startingFrameId = currentFrame + (TickEngineSettings.targetBufferSize /*+ 1*/);
00206             while (startingFrameId >= frameCount)
00207                 startingFrameId -= frameCount;
00208 
00209             int originToLocal = startingFrameId - originFrameId;
00210             if (originToLocal < 0)
00211                 originToLocal += frameCount;
00212 
00213             int localToOrigin = frameCount - originToLocal;
00214             if (localToOrigin < 0)
00215                 localToOrigin += frameCount;
00216 
00219             offsets = new ConnectionTickOffsets(connId, originToLocal, localToOrigin);
00220 
00221             AddConnection(connId, offsets);
00222 
00223         }
00224 
00225         private static void AddConnection(int connId, ConnectionTickOffsets offsets = null)
00226         {
00227 #if PUN_2_OR_NEWER
00229             if (PhotonNetwork.LocalPlayer.ActorNumber == connId)
00230                 return;
00231 #endif
00232             if (!connections.Contains(connId))
00233             {
00234                 perConnOffsets.Add(connId, offsets);
00235                 connections.Add(connId);
00237                 NetMsgSends.newPlayers.Add(connId);
00238                 needToSendInitialForNewConn = true;
00239             }
00240             else
00241             {
00242                 perConnOffsets[connId] = offsets;
00243             }
00244         }
00245 
00246         public static void RemoveConnection(int connId)
00247         {
00248             if (perConnOffsets.ContainsKey(connId))
00249             {
00250                 perConnOffsets.Remove(connId);
00251                 connections.Remove(connId);
00252             }
00253         }
