00120         public void SetTickInterval(float tickInterval)
00121         {
00122             // Convert seconds into ticks for a more deterministic and networkable count
00123             _decayDelayInTicks = (int)(decayDelay / tickInterval);
00124             _regenDelayInTicks = (int)(regenDelay / tickInterval);
00125             _decayPerTick = decayRate * tickInterval;
00126             _regenPerTick = regenRate * tickInterval;
00127 
00128             bitsForValue = _maxValue.GetBitsForMaxValue();
00129             bitsForDecayDelay = _decayDelayInTicks.GetBitsForMaxValue();
00130             bitsForRegenDelay = _regenDelayInTicks.GetBitsForMaxValue();
00131         }
00132 
00133         public VitalData GetDefaultData()
