00138         public SerializationFlags Serialize(VitalData vitalData, VitalData prevVitalData, byte[] buffer, ref int bitposition, bool keyframe = true)
00139         {
00140             var ticksuntildecay = vitalData.ticksUntilDecay;
00141             var ticksuntilregen = vitalData.ticksUntilRegen;
00142 
00143             //if (keyframe)
00144             //{
00145             //  buffer.Write((ulong)vitalData.Value, ref bitposition, bitsForValue);
00146             //  return SerializationFlags.HasChanged;
00147             //}
00148             //else
00149             int newval = (int)vitalData.Value;
00150             int prevval = (int)prevVitalData.Value;
00151 
00152             bool haschanged = newval != prevval;
00153 
00154             if (keyframe)
00155             {
00156                 buffer.Write((ulong)newval, ref bitposition, bitsForValue);
00157             }
00158             else
00159             {
00160                 buffer.WriteBool(haschanged, ref bitposition);
00161                 if (haschanged)
00162                     buffer.Write((ulong)newval, ref bitposition, bitsForValue);
00163             }
00164 
00166             if (ticksuntildecay > 0)
00167             {
00168                 buffer.WriteBool(true, ref bitposition);
00169                 buffer.Write((ulong)ticksuntildecay, ref bitposition, bitsForDecayDelay);
00170             }
00171             else
00172                 buffer.WriteBool(false, ref bitposition);
00173 
00174             if (ticksuntilregen > 0)
00175             {
00176                 buffer.WriteBool(true, ref bitposition);
00177                 buffer.Write((ulong)ticksuntilregen, ref bitposition, bitsForRegenDelay);
00178             }
00179             else
00180                 buffer.WriteBool(false, ref bitposition);
00181 
00182             return (haschanged || keyframe) ? SerializationFlags.HasContent : SerializationFlags.None;
00183 
00184         }
00185 
00186         public VitalData Deserialize(byte[] buffer, ref int bitposition, bool keyframe = true)
00187         {
00188             double val = (keyframe || buffer.ReadBool(ref bitposition)) ? (double)buffer.Read(ref bitposition, bitsForValue) : double.NegativeInfinity;
00189             VitalData data = new VitalData(
00190                 val,
00191                 (int)(buffer.ReadBool(ref bitposition) ? buffer.Read(ref bitposition, bitsForDecayDelay) : 0),
00192                 (int)(buffer.ReadBool(ref bitposition) ? buffer.Read(ref bitposition, bitsForRegenDelay) : 0)
00193             );
00194 
00195             return data;
00196         }
00197 
00198         public VitalData Extrapolate(VitalData prev)
00199         {
00200             int ticksuntilregen = prev.ticksUntilRegen > 0 ? prev.ticksUntilRegen - 1 : 0;
00201             int ticksuntildecay = prev.ticksUntilDecay > 0 ? prev.ticksUntilDecay - 1 : 0;
00202             double preval = prev.Value;
00203             double val =
00204                 (preval > FullValue && ticksuntildecay == 0) ? preval - _decayPerTick :
00205                 (preval < FullValue && ticksuntilregen == 0) ? preval + _regenPerTick :
00206                 preval;
00207 
00208             return new VitalData(val, ticksuntildecay, ticksuntilregen);
00209         }
