00061         public void Initialize()
00062         {
00063             if (initialized)
00064                 return;
00065 
00066 
00068             vitalCount = vitalDefs.Count;
00069             vitalArray = new Vital[vitalCount];
00070 
00071             for (int i = 0; i < vitalCount; ++i)
00072             {
00073                 VitalDefinition vdef = vitalDefs[i];
00074                 Vital vital = new Vital(vdef);
00075                 vitalArray[i] = vital;
00076 
00077                 vital.Initialize(TickEngineSettings.netTickInterval);
00078                 vital.AddIOnVitalChange(this);
00079 
00080                 // add all of the vital types to the lookup. Customs use hash, enumerated are added both as hash and by their index
00081                 int hash = vdef.VitalName.hash;
00082                 if (vdef.VitalName.type != VitalType.None)
00083                 {
00084                     if (!vitalLookup.ContainsKey(hash))
00085                         vitalLookup.Add(hash, vital);
00086                     else
00087                         Debug.LogWarning("VitalNameType hash collision! Vitals cannot have more than one of each Vital Type in its list.");
00088                 }
00089             }
00090 
00091             initialized = true;
00092         }
