00149         public SerializationFlags Deserialize(VitalsData vdata, byte[] buffer, ref int bitposition, bool keyframe)
00150         {
00151             var datas = vdata.datas;
00152 
00153             bool isComplete = true;
00154             bool hasChanged = false;
00155 
00156             for (int i = 0; i < vitalCount; ++i)
00157             {
00158                 datas[i] = vitalDefs[i].Deserialize(buffer, ref bitposition, keyframe);
00159 
00161                 if (datas[i].Value == double.NegativeInfinity)
00162                     isComplete = false;
00163                 else
00164                     hasChanged |= true;
00165             }
00166 
00167             return
00168                 isComplete ? SerializationFlags.IsComplete | SerializationFlags.HasContent :
00169                 hasChanged ? SerializationFlags.HasContent :
00170                 SerializationFlags.None;
00171         }
