00208         private double ApplyCharges(int vitalIdx, double discharge, bool allowOverload, bool propagate)
00209         {
00210 
00211             double remaining = discharge;
00212             double consumed = 0;
00213 
00215             for (int i = vitalIdx; i >= 0; --i)
00216             {
00217                 consumed += vitalArray[i].ApplyCharges(remaining, allowOverload, !propagate);
00218 
00219                 if (propagate)
00220                 {
00221                     remaining -= consumed;
00222 
00223                     if (remaining == 0)
00224                         break;
00225                 }
00226                 else
00227                     return consumed;
00228             }
00229 
00230             return consumed;
00231         }
00232 
00233         public double ApplyCharges(double discharge, bool allowOverload, bool propagate)
00234         {
00235             return ApplyCharges(vitalCount - 1, discharge, allowOverload, propagate);
00236         }
