00238         public double ApplyCharges(VitalNameType vitalNameType, double discharge, bool allowOverload, bool propagate)
00239         {
00240             var vtype = vitalNameType.type;
00241 
00242             int vitalIdx;
00243 
00244             // Type None indicates no specific Vital, so this value should be applied to any complete stack.
00245             if (vtype == VitalType.None)
00246                 vitalIdx = vitalCount - 1;
00247             else
00248             {
00249                 vitalIdx = GetVitalIndex(vitalNameType);
00250 
00251                 if (vitalIdx == -1)
00252                     return 0;
00253             }
00254 
00255             double consumed = ApplyCharges(vitalIdx, discharge, allowOverload, propagate);
00256 
00257             CheckForDisrupt(consumed);
00258 
00259             return consumed;
00260         }
00261 
00262         public void CheckForDisrupt(double consumed)
00263         {
00264             if (consumed == 0)
00265                 return;
00266 
00267             if (consumed > 0)
00268                 DisruptDecay();
00269 
00270             if (consumed < 0)
00271                 DisruptRegen();
00272         }
