00088         public static int TallyBits(ref int index, BitCullingLevel bcl = BitCullingLevel.NoCulling)
00089         {
00090             var wbs = Single.worldBoundsGroups;
00091 
00092             // if WorldBounds layer is no longer valid, reset to default
