00228         private static HashSet<string> namecheck = new HashSet<string>();
00229         public static void EnsureNamesAreUnique(int newestIndex = -1)
00230         {
00231             namecheck.Clear();
00232             var wbs = Single.worldBoundsGroups;
00233             bool haschanged = false;
00234 
00235             for (int i = 0; i < wbs.Count; ++i)
00236             {
00237                 var wbsi = wbs[i];
00238                 // Try adding newest changed last, so it will get its named changed, rather than an existing duplicate
00239                 if (i == newestIndex && i != 0)
00240                     continue;
00241 
00242                 if (i == 0)
00243                 {
00244                     if (wbsi.name != WorldBoundsGroup.defaultName)
00245                     {
00246                         haschanged = true;
00247                         wbsi.name = WorldBoundsGroup.defaultName;
00248                     }
00249                 }
00250                 else
00251                     while (namecheck.Contains(wbsi.name))
00252                     {
00253                         haschanged = true;
00254                         wbsi.name += "X";
00255                     }
00256 
00257                 namecheck.Add(wbsi.name);
00258             }
00259 
00260             // Change the newest changed name as needed (if supplied and valid) last
00261             if (newestIndex > 0 && newestIndex < wbs.Count)
00262                 while (namecheck.Contains(wbs[newestIndex].name))
00263                 {
00264                     haschanged = true;
00265                     wbs[newestIndex].name += "X";
00266                 }
00267 
00268             if (haschanged)
00269             {
00270                 EditorUtility.SetDirty(Single);
00271             }
00272         }
